package com.udemy.spring.springselenium.entity;public class PaymentInfo {    private PaymentTypeEnum paymentType;    private String amount;    private LightBoxCredentials lightBoxCredentials = new LightBoxCredentials();    public PaymentInfo(PaymentTypeEnum paymentType, String amount) {        this.paymentType = paymentType;        this.amount = amount;    }    public PaymentTypeEnum getPaymentType() {        return paymentType;    }    public void setPaymentType(PaymentTypeEnum paymentType) {        this.paymentType = paymentType;    }    public String getAmount() {        return amount;    }    public void setAmount(String amount) {        this.amount = amount;    }    public LightBoxCredentials getLightBoxCredentials() {        return lightBoxCredentials;    }    public void setLightBoxCredentials(LightBoxCredentials lightBoxCredentials) {        this.lightBoxCredentials = lightBoxCredentials;    }}